<script>
import {
  DiagramComponent,
  Diagram,
  DiagramTools,
  NodeConstraints,
} from "@syncfusion/ej2-vue-diagrams";
import {
  Node,
  DataBinding,
  RadialTree,
  SnapConstraints
} from "@syncfusion/ej2-vue-diagrams";
import { DataManager } from "@syncfusion/ej2-data";
import { radialTree } from "./diagram-data";
import { ToolbarComponent } from "@syncfusion/ej2-vue-navigations";
Diagram.Inject(DataBinding, RadialTree);

let diagramInstance;

export default {
  components: {
    // Defines components for the diagram and toolbar
    'ejs-diagram': DiagramComponent,
    'ejs-toolbar': ToolbarComponent
  },
  data: function() {
    return {
      // Initialize component data
      width: "100%",
      height: "600px",
      snapSettings: { constraints: SnapConstraints.None },
      //configures data source settings
      dataSourceSettings: {
        //sets the fields to bind
        id: "Id",
        parentId: "ReportingPerson",
        dataSource: new DataManager(radialTree),
        //binds the data to the nodes
        doBinding: (nodeModel, data, diagram) => {
          nodeModel.annotations = [
            {
              content: data.Name,
              style:
                data.Id === "parent"
                  ? { color: "white", fontSize: 50 }
                  : { color: "black", fontSize: 20 }
            }
          ];
          nodeModel.constraints =
            (NodeConstraints.Default) |
            NodeConstraints.Tooltip;
          nodeModel.tooltip = {
            content:
              "<h6>" + data.Name + "</h6><p>" + data.Designation + "</p>",
            relativeMode: "Object",
            position: "TopCenter",
            showTipPointer: true
          };
          if (data.Designation === "Managing Director") {
            nodeModel.width = 400;
            nodeModel.height = 400;
            nodeModel.shape = { shape: "Ellipse" };
            nodeModel.style = { fill: "black" };
          } else if (data.Designation === "Project Manager") {
            nodeModel.width = 130;
            nodeModel.height = 130;
            nodeModel.height = 130;
            nodeModel.style = { fill: "#f8ab52" };
          } else {
            nodeModel.width = 100;
            nodeModel.height = 100;
            nodeModel.shape = { shape: "Ellipse" };
            nodeModel.style = { fill: "#afeeee" };
          }
        }
      },
      //Disables all interactions except zoom and pan
      tool: DiagramTools.ZoomPan,
      //Configures automatic layout
      layout: {
        type: "RadialTree",
        verticalSpacing: 30,
        horizontalSpacing: 20,
        root: "Category"
      },
      toolbarclicked: onItemClick,
      toolbaritems: [
        {
          type: "Button",
          tooltipText: "ZoomIn",
          text: "Zoom In",
          prefixIcon:'e-icons e-zoom-in'
        },
        {
          type: "Separator"
        },
        {
          type: "Button",
          tooltipText: "ZoomOut",
          text: "Zoom Out",
          prefixIcon:'e-icons e-zoom-out'
        },
        {
          type: "Separator"
        },
        {
          type: "Button",
          tooltipText: "Reset",
          text: "Reset",
          prefixIcon : 'e-diagram-icons e-diagram-reset'
        }
      ]
    };
  },
  provide: {
    diagram: [DataBinding, RadialTree]
  },
  mounted: function() {
    diagramInstance = this.$refs.diagramObj.ej2Instances;
    diagramInstance.fitToPage();
  }
};

// Handles click events to perform ZoomIn, ZoomOut, and Reset based on the selected option.
function onItemClick(args) {
  switch (args.item.text) {
    case "Zoom In":
      let zoomin = { type: "ZoomIn", zoomFactor: 0.2 };
      diagramInstance.zoomTo(zoomin);
      break;
    case "Zoom Out":
      let zoomout = { type: "ZoomOut", zoomFactor: 0.2 };
      diagramInstance.zoomTo(zoomout);
      break;
    case "Reset":
      diagramInstance.reset();
      diagramInstance.fitToPage();
      break;
  }
}
</script>