  methods: {
    btnClick: function() {
      this.applyDateFilter();
    },
    onLoad: function() {
      this.applyDateFilter();
    },
    startChange: function(args: any) {
        startDate = args.value;
    },
    endChange: function(args: any) {
        endDate = args.value;
    },
    applyDateFilter: function() {
      if (startDate && endDate) {
        startDate.setHours(0, 0, 0, 0);
        endDate.setHours(23, 59, 59, 999);
        let Pivot_Data = (pivotData as any).map((item: any) => ({
            ...item,
            OrderDate: new Date(item.OrderDate),
        }));
        new DataManager({ json: Pivot_Data, adaptor: new JsonAdaptor() }).executeQuery(
            new Query()
                .where('OrderDate', 'greaterthanorequal', startDate)
                .where('OrderDate', 'lessthanorequal', endDate)
        )
        .then((e: any) => {
          let pivotObj = ((this as any).$refs.PivotView).ej2Instances;
          pivotObj.dataSourceSettings.dataSource = (e as any).result;
        });
      }
    }
  }
